Task[] tasks;

foreach (i; 0 .. 100) {
	tasks ~= runWorkerTaskH(() nothrow {
		auto l = scopedMutexLock(mutex);
		counter++;
	});
}

foreach (t; tasks) t.join();

assert(counter == 100);

