union Fields {
	int number;
	string text;
}

TaggedAlgebraic!Fields ta = "test";

assert(ta.hasType!string);
assert(!ta.hasType!int);

ta = 42;
assert(ta.hasType!int);
assert(!ta.hasType!string);

