import vibe.core.net;
import vibe.stream.tls;

void sendTLSMessage()
{
	auto conn = connectTCP("127.0.0.1", 1234);
	auto sslctx = createTLSContext(TLSContextKind.client);
	auto stream = createTLSStream(conn, sslctx);
	stream.write("Hello, World!");
	stream.finalize();
	conn.close();
}

